import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { FiPlus, FiEdit, FiTrash2, FiUser, FiCalendar, FiFlag } from 'react-icons/fi';
import axios from 'axios';
import { toast } from 'react-toastify';

const TasksContainer = styled.div`
  max-width: 1200px;
  margin: 0 auto;
`;

const PageHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 32px;
`;

const PageTitle = styled.h1`
  font-size: 2rem;
  font-weight: 700;
  color: #2c3e50;
`;

const TasksGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(350px, 1fr));
  gap: 24px;
`;

const TaskCard = styled.div`
  background: white;
  border-radius: 12px;
  box-shadow: 0 2px 8px rgba(0, 0, 0, 0.1);
  padding: 20px;
  transition: transform 0.2s, box-shadow 0.2s;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 4px 16px rgba(0, 0, 0, 0.15);
  }
`;

const TaskHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: start;
  margin-bottom: 16px;
`;

const TaskTitle = styled.h3`
  font-size: 1.125rem;
  font-weight: 600;
  color: #2c3e50;
  margin-bottom: 8px;
`;

const TaskActions = styled.div`
  display: flex;
  gap: 8px;
`;

const ActionButton = styled.button`
  background: none;
  border: none;
  padding: 6px;
  border-radius: 4px;
  cursor: pointer;
  color: #6b7280;
  transition: all 0.2s;

  &:hover {
    background: #f3f4f6;
    color: #374151;
  }
`;

const StatusBadge = styled.span`
  padding: 4px 12px;
  border-radius: 20px;
  font-size: 0.75rem;
  font-weight: 500;
  
  &.todo {
    background: #f3f4f6;
    color: #374151;
  }
  
  &.in-progress {
    background: #dbeafe;
    color: #1d4ed8;
  }
  
  &.completed {
    background: #dcfce7;
    color: #166534;
  }
  
  &.on-hold {
    background: #fef3c7;
    color: #92400e;
  }
`;

const PriorityBadge = styled.span`
  padding: 4px 8px;
  border-radius: 4px;
  font-size: 0.75rem;
  font-weight: 500;
  
  &.low {
    background: #f0f9ff;
    color: #0369a1;
  }
  
  &.medium {
    background: #fef3c7;
    color: #92400e;
  }
  
  &.high {
    background: #fef2f2;
    color: #dc2626;
  }
  
  &.urgent {
    background: #991b1b;
    color: white;
  }
`;

const TaskDescription = styled.p`
  color: #6b7280;
  margin-bottom: 16px;
  line-height: 1.5;
`;

const TaskMeta = styled.div`
  display: flex;
  align-items: center;
  gap: 16px;
  font-size: 0.875rem;
  color: #6b7280;
  margin-bottom: 12px;
`;

const MetaItem = styled.div`
  display: flex;
  align-items: center;
  gap: 4px;
`;

const ProjectTag = styled.div`
  display: inline-flex;
  align-items: center;
  padding: 4px 8px;
  background: ${props => props.color || '#3498db'}20;
  color: ${props => props.color || '#3498db'};
  border-radius: 4px;
  font-size: 0.75rem;
  font-weight: 500;
`;

const AddButton = styled.button`
  background: #3498db;
  color: white;
  border: none;
  padding: 12px 20px;
  border-radius: 8px;
  font-weight: 500;
  cursor: pointer;
  display: flex;
  align-items: center;
  gap: 8px;
  transition: background-color 0.2s;

  &:hover {
    background: #2980b9;
  }
`;

const Modal = styled.div`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
`;

const ModalContent = styled.div`
  background: white;
  border-radius: 12px;
  padding: 24px;
  width: 100%;
  max-width: 500px;
  margin: 20px;
`;

const ModalHeader = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 24px;
`;

const ModalTitle = styled.h2`
  font-size: 1.5rem;
  font-weight: 600;
  color: #2c3e50;
`;

const CloseButton = styled.button`
  background: none;
  border: none;
  font-size: 1.5rem;
  cursor: pointer;
  color: #6b7280;

  &:hover {
    color: #374151;
  }
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 16px;
`;

const FormGroup = styled.div`
  display: flex;
  flex-direction: column;
`;

const Label = styled.label`
  margin-bottom: 8px;
  font-weight: 500;
  color: #374151;
`;

const Input = styled.input`
  padding: 12px;
  border: 1px solid #d1d5db;
  border-radius: 6px;
  font-size: 14px;

  &:focus {
    outline: none;
    border-color: #3498db;
    box-shadow: 0 0 0 3px rgba(52, 152, 219, 0.1);
  }
`;

const Textarea = styled.textarea`
  padding: 12px;
  border: 1px solid #d1d5db;
  border-radius: 6px;
  font-size: 14px;
  resize: vertical;
  min-height: 100px;

  &:focus {
    outline: none;
    border-color: #3498db;
    box-shadow: 0 0 0 3px rgba(52, 152, 219, 0.1);
  }
`;

const Select = styled.select`
  padding: 12px;
  border: 1px solid #d1d5db;
  border-radius: 6px;
  font-size: 14px;
  background: white;

  &:focus {
    outline: none;
    border-color: #3498db;
    box-shadow: 0 0 0 3px rgba(52, 152, 219, 0.1);
  }
`;

const ModalActions = styled.div`
  display: flex;
  justify-content: flex-end;
  gap: 12px;
  margin-top: 24px;
`;

const Button = styled.button`
  padding: 10px 16px;
  border-radius: 6px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s;

  &.primary {
    background: #3498db;
    color: white;
    border: none;

    &:hover {
      background: #2980b9;
    }
  }

  &.secondary {
    background: #f8f9fa;
    color: #374151;
    border: 1px solid #d1d5db;

    &:hover {
      background: #e9ecef;
    }
  }

  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
  }
`;

const Tasks = () => {
  const [tasks, setTasks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [showModal, setShowModal] = useState(false);
  const [editingTask, setEditingTask] = useState(null);
  const [projects, setProjects] = useState([]);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    priority: 'medium',
    status: 'todo',
    projectId: '',
    dueDate: ''
  });

  useEffect(() => {
    fetchTasks();
    fetchProjects();
  }, []);

  const fetchTasks = async () => {
    try {
      const response = await axios.get('/tasks');
      setTasks(response.data);
    } catch (error) {
      toast.error('Failed to fetch tasks');
      console.error('Error fetching tasks:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchProjects = async () => {
    try {
      const response = await axios.get('/projects');
      setProjects(response.data);
    } catch (error) {
      console.error('Error fetching projects:', error);
    }
  };  // Helper function to display detailed error messages
  const handleApiError = (error, defaultMessage) => {
    if (error.response?.data) {
      const errorData = error.response.data;
      
      if (errorData.details && errorData.action) {
        // Show detailed error with action steps
        toast.error(
          <div>
            <div style={{ fontWeight: 'bold', marginBottom: '8px' }}>{errorData.message}</div>
            <div style={{ marginBottom: '8px' }}>{errorData.details}</div>
            {errorData.projectInfo && (
              <div style={{ marginBottom: '8px', fontSize: '0.9em' }}>
                <strong>Project:</strong> {errorData.projectInfo.name}<br/>
                <strong>Owner:</strong> {errorData.projectInfo.owner}<br/>
                <strong>Members:</strong> {errorData.projectInfo.members}
              </div>
            )}
            <div style={{ fontStyle: 'italic', color: '#666' }}>
              {errorData.action}
            </div>
            {errorData.solution && (
              <div style={{ marginTop: '8px', padding: '8px', background: '#f0f9ff', borderRadius: '4px', fontSize: '0.9em' }}>
                <strong>Solution:</strong> {errorData.solution}
              </div>
            )}
          </div>,
          {
            autoClose: 8000, // Show longer for detailed messages
            style: { width: '400px' }
          }
        );
      } else if (errorData.message) {
        toast.error(errorData.message);
      } else {
        toast.error(defaultMessage);
      }
    } else {
      toast.error(defaultMessage);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.projectId) {
      toast.error('Please select a project');
      return;
    }
      try {
      if (editingTask && editingTask._id) {
        await axios.put(`/tasks/${editingTask._id}`, {
          ...formData,
          project: formData.projectId
        });
        toast.success('Task updated successfully');
      } else {
        await axios.post('/tasks', {
          ...formData,
          project: formData.projectId
        });
        toast.success('Task created successfully');
      }
      fetchTasks();
      closeModal();
    } catch (error) {
      handleApiError(error, editingTask ? 'Failed to update task' : 'Failed to create task');
      console.error('Error saving task:', error);
    }
  };

  const openModal = (task = null) => {
    if (task) {
      setEditingTask(task);
      setFormData({
        name: task.name,
        description: task.description || '',
        priority: task.priority,
        status: task.status,
        projectId: task.project?._id || '',
        dueDate: task.dueDate ? task.dueDate.split('T')[0] : ''
      });
    } else {
      setEditingTask(null);
      setFormData({
        name: '',
        description: '',
        priority: 'medium',
        status: 'todo',
        projectId: '',
        dueDate: ''
      });
    }
    setShowModal(true);
  };
  const closeModal = () => {
    setShowModal(false);
    setEditingTask(null);
    setFormData({
      name: '',
      description: '',
      priority: 'medium',
      status: 'todo',
      projectId: '',
      dueDate: ''
    });
  };

  const handleDelete = async (taskId) => {
    if (window.confirm('Are you sure you want to delete this task?')) {
      try {
        await axios.delete(`/tasks/${taskId}`);
        toast.success('Task deleted successfully');
        fetchTasks();
      } catch (error) {
        toast.error('Failed to delete task');
        console.error('Error deleting task:', error);
      }
    }
  };

  const handleFormChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const getStatusLabel = (status) => {
    const labels = {
      'todo': 'To Do',
      'in-progress': 'In Progress',
      'completed': 'Completed',
      'on-hold': 'On Hold'
    };
    return labels[status] || status;
  };

  const getPriorityLabel = (priority) => {
    const labels = {
      'low': 'Low',
      'medium': 'Medium',
      'high': 'High',
      'urgent': 'Urgent'
    };
    return labels[priority] || priority;
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="loading-spinner"></div>
      </div>
    );
  }

  return (
    <TasksContainer>
      <PageHeader>
        <PageTitle>Tasks</PageTitle>        <AddButton onClick={() => openModal(null)}>
          <FiPlus size={20} />
          New Task
        </AddButton>
      </PageHeader>

      <TasksGrid>
        {tasks.map(task => (
          <TaskCard key={task._id}>
            <TaskHeader>
              <div>
                <TaskTitle>{task.name}</TaskTitle>
                <div style={{ display: 'flex', gap: '8px', alignItems: 'center' }}>
                  <StatusBadge className={task.status}>
                    {getStatusLabel(task.status)}
                  </StatusBadge>
                  <PriorityBadge className={task.priority}>
                    <FiFlag size={12} style={{ marginRight: '4px' }} />
                    {getPriorityLabel(task.priority)}
                  </PriorityBadge>
                </div>
              </div>              <TaskActions>
                <ActionButton onClick={() => openModal(task)}>
                  <FiEdit size={16} />
                </ActionButton>
                <ActionButton onClick={() => handleDelete(task._id)}>
                  <FiTrash2 size={16} />
                </ActionButton>
              </TaskActions>
            </TaskHeader>

            {task.description && (
              <TaskDescription>{task.description}</TaskDescription>
            )}

            <TaskMeta>
              {task.assignee && (
                <MetaItem>
                  <FiUser size={14} />
                  {task.assignee.name}
                </MetaItem>
              )}
              {task.dueDate && (
                <MetaItem>
                  <FiCalendar size={14} />
                  {new Date(task.dueDate).toLocaleDateString()}
                </MetaItem>
              )}
            </TaskMeta>

            {task.project && (
              <ProjectTag color={task.project.color}>
                {task.project.name}
              </ProjectTag>
            )}
          </TaskCard>
        ))}
      </TasksGrid>

      {tasks.length === 0 && !loading && (
        <div style={{ textAlign: 'center', padding: '64px 0', color: '#6b7280' }}>
          <p>No tasks found. Create your first task to get started!</p>
        </div>
      )}

      {showModal && (
        <Modal>          <ModalContent>
            <ModalHeader>
              <ModalTitle>{editingTask ? 'Edit Task' : 'Create New Task'}</ModalTitle>
              <CloseButton onClick={closeModal}>&times;</CloseButton>
            </ModalHeader>

            <Form onSubmit={handleSubmit}>
              <FormGroup>
                <Label htmlFor="task-name">Task Name</Label>
                <Input
                  id="task-name"
                  value={formData.name}
                  onChange={(e) => handleFormChange('name', e.target.value)}
                  required
                />
              </FormGroup>

              <FormGroup>
                <Label htmlFor="task-description">Description</Label>
                <Textarea
                  id="task-description"
                  value={formData.description}
                  onChange={(e) => handleFormChange('description', e.target.value)}
                />
              </FormGroup>

              <FormGroup>
                <Label htmlFor="task-priority">Priority</Label>
                <Select
                  id="task-priority"
                  value={formData.priority}
                  onChange={(e) => handleFormChange('priority', e.target.value)}
                >
                  <option value="low">Low</option>
                  <option value="medium">Medium</option>
                  <option value="high">High</option>
                  <option value="urgent">Urgent</option>
                </Select>
              </FormGroup>

              <FormGroup>
                <Label htmlFor="task-status">Status</Label>
                <Select
                  id="task-status"
                  value={formData.status}
                  onChange={(e) => handleFormChange('status', e.target.value)}
                >
                  <option value="todo">To Do</option>
                  <option value="in-progress">In Progress</option>
                  <option value="completed">Completed</option>
                  <option value="on-hold">On Hold</option>
                </Select>
              </FormGroup>              <FormGroup>
                <Label htmlFor="task-project">Project *</Label>
                <Select
                  id="task-project"
                  value={formData.projectId}
                  onChange={(e) => handleFormChange('projectId', e.target.value)}
                  required
                >
                  <option value="">Select a project</option>
                  {projects.map(project => (
                    <option key={project._id} value={project._id}>
                      {project.name}
                    </option>
                  ))}
                </Select>
              </FormGroup>

              <FormGroup>
                <Label htmlFor="task-due-date">Due Date</Label>
                <Input
                  id="task-due-date"
                  type="date"
                  value={formData.dueDate}
                  onChange={(e) => handleFormChange('dueDate', e.target.value)}
                />
              </FormGroup>              <ModalActions>
                <Button type="button" className="secondary" onClick={closeModal}>
                  Cancel
                </Button>
                <Button type="submit" className="primary">
                  {editingTask ? 'Update Task' : 'Create Task'}
                </Button>
              </ModalActions>
            </Form>
          </ModalContent>
        </Modal>
      )}
    </TasksContainer>
  );
};

export default Tasks;
